import logging
from data.config import dict_server, dict_tariff


async def get_profile_data(user_data) -> str:
    try:
        days = user_data['balance'] // (user_data['count_configs'] * user_data['sum_tariff'])
    except ZeroDivisionError:
        days = '–ë–µ—Å–∫–æ–Ω–µ—á–Ω–æ'
    text_profile = f"<b>–í–∞—à –ª–∏—á–Ω—ã–π –∫–∞–±–∏–Ω–µ—Ç</b>\n–†–∞–¥—ã –≤—ã–¥–µ—Ç—å –≤–∞—Å —Å–Ω–æ–≤–∞!\n\n<b>–í–∞—à id:</b> <code><b>{user_data['user_id']}</b></code> \n(–Ω–∞–∂–º–∏ —á—Ç–æ–±—ã —Å–∫–æ–ø–∏—Ä–æ–≤–∞—Ç—å)\n<b>–ë–∞–ª–∞–Ω—Å:</b> <b>{user_data['balance']}</b> —Ä—É–±.\n<b>–¢–∞—Ä–∏—Ñ:</b> <b>{user_data['sum_tariff']}</b> —Ä—É–±/—Å—É—Ç–∫–∏\n–•–≤–∞—Ç–∏—Ç –Ω–∞ <b>{days}</b> –¥–Ω—è(–µ–π) \n\n<b>–û–ø–∏—Å–∞–Ω–∏–µ –∫–Ω–æ–ø–æ–∫:</b> \n\nüíª<b>–ú–æ–∏ —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞:</b> –¢—É—Ç –≤—ã –º–æ–∂–µ—Ç–µ —Å–æ–∑–¥–∞–≤–∞—Ç—å, –ø—Ä–æ—Å–º–∞—Ç—Ä–∏–≤–∞—Ç—å –∏ —É–¥–∞–ª—è—Ç—å –≤–∞—à–∏ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏—è/—É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞. \n\nüí≥<b>–ü–æ–ø–æ–ª–Ω–∏—Ç—å –±–∞–ª–∞–Ω—Å:</b> –ü–æ–ø–æ–ª–Ω–µ–Ω–∏–µ –±–∞–ª–∞–Ω—Å–∞ —è–≤–ª—è–µ—Ç—Å—è –æ–¥–Ω–æ–∫—Ä–∞—Ç–Ω–æ–π –æ–ø–µ—Ä–∞—Ü–∏–µ–π <b>(–Ω–µ –ø–æ–¥–ø–∏—Å–∫–∞)</b>. –ú—ã –Ω–µ –∏–º–µ–µ–º –¥–æ—Å—Ç—É–ø–∞ –∫ –≤–∞—à–∏–º –ª–∏—á–Ω—ã–º –∏ –ø–ª–∞—Ç–µ–∂–Ω—ã–º –¥–∞–Ω–Ω—ã–º.\n\nüÜò<b>–ü–æ–º–æ—â—å:</b> –ù–∞–ø–∏—Å–∞—Ç—å –æ–±—Ä–∞—â–µ–Ω–∏–µ –≤ –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ü–∏—é —Å–µ—Ä–≤–∏—Å–∞."
    if user_data['friend']:
        text_profile = f"<b>–í–∞—à –ª–∏—á–Ω—ã–π –∫–∞–±–∏–Ω–µ—Ç</b>\n–†–∞–¥—ã –≤–∏–¥–µ—Ç—å –≤–∞—Å —Å–Ω–æ–≤–∞!\n\n<b>–í–∞—à id:</b> <code><b>{user_data['user_id']}</b></code> \n(–Ω–∞–∂–º–∏ —á—Ç–æ–±—ã —Å–∫–æ–ø–∏—Ä–æ–≤–∞—Ç—å)\n<b>–í–∞—à —Å—Ç–∞—Ç—É—Å: Free </b>\n\n<b>–û–ø–∏—Å–∞–Ω–∏–µ –∫–Ω–æ–ø–æ–∫:</b> \n\nüíª<b>–ú–æ–∏ —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞:</b> –¢—É—Ç –≤—ã –º–æ–∂–µ—Ç–µ —Å–æ–∑–¥–∞–≤–∞—Ç—å, –ø—Ä–æ—Å–º–∞—Ç—Ä–∏–≤–∞—Ç—å –∏ —É–¥–∞–ª—è—Ç—å –≤–∞—à–∏ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏—è/—É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞. \n\nüí≥<b>–ü–æ–ø–æ–ª–Ω–∏—Ç—å –±–∞–ª–∞–Ω—Å:</b> –ü–æ–ø–æ–ª–Ω–µ–Ω–∏–µ –±–∞–ª–∞–Ω—Å–∞ —è–≤–ª—è–µ—Ç—Å—è –æ–¥–Ω–æ–∫—Ä–∞—Ç–Ω–æ–π –æ–ø–µ—Ä–∞—Ü–∏–µ–π <b>(–Ω–µ –ø–æ–¥–ø–∏—Å–∫–∞)</b>. –ú—ã –Ω–µ –∏–º–µ–µ–º –¥–æ—Å—Ç—É–ø–∞ –∫ –≤–∞—à–∏–º –ª–∏—á–Ω—ã–º –∏ –ø–ª–∞—Ç–µ–∂–Ω—ã–º –¥–∞–Ω–Ω—ã–º.\n\nüÜò<b>–ü–æ–º–æ—â—å:</b> –ù–∞–ø–∏—Å–∞—Ç—å –æ–±—Ä–∞—â–µ–Ω–∏–µ –≤ –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ü–∏—é —Å–µ—Ä–≤–∏—Å–∞."
    return text_profile

    

async def get_profile_user_device(device_info) -> str:
    try:
        text_device = f"–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –≤–∞—à–µ–º –∫–æ–Ω—Ñ–∏–≥–µ\n\n–ò–º—è –∫–æ–Ω—Ñ–∏–≥–∞: <b>{device_info['config_name']}</b>\n–¢–∞—Ä–∏—Ñ: <b>{dict_tariff[device_info['tariff']]}</b>\n–°—Ç—Ä–∞–Ω–∞: <b>{dict_server[device_info['number_server']]['country']}</b>"
    except Exception as e:
        text_device = f"–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –≤–∞—à–µ–º –∫–æ–Ω—Ñ–∏–≥–µ\n\n–ò–º—è –∫–æ–Ω—Ñ–∏–≥–∞: <b>{device_info['config_name']}</b>\n–¢–∞—Ä–∏—Ñ: <b>STANDARD</b>\n–°—Ç—Ä–∞–Ω–∞: <b>{dict_server[device_info['number_server']]['country']}</b>"
    return text_device
